definition.script:
node{
   bat(/rd /s /q %userprofile%\Deploy/)
   bat(/mkdir %userprofile%\Deploy/)
   dir('discovery'){
       stage('checkOut') { // for display purposes
          // Get some code from a GitHub repository
          git([url: '{{discovery}}', branch: 'master'])          
       }
       stage('Build') {
          bat(/"gradlew" clean build/)
       }
       stage('Deploy-local') {
            bat(/copy build\libs\discovery-0.0.1-SNAPSHOT.jar  %userprofile%\Deploy\discovery.jar/)
            withEnv(['JENKINS_NODE_COOKIE=dontkill']) {
                  bat(/start java -jar %userprofile%\Deploy\discovery.jar/)
          } 
         }
   }
    dir('gateway'){
        stage('checkOut') { // for display purposes
        // Get some code from a GitHub repository
        git([url: '{{gateway}}', branch: 'master'])          
      }
      stage('Build') {
          bat(/"gradlew" clean build/)
